import { Select, TextField } from '@material-ui/core'
import React,{forwardRef, useImperativeHandle, useState} from 'react'
import { KeyboardDatePicker,MuiPickersUtilsProvider
} from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
import { makeStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
const useStyles = makeStyles((theme) => ({
    container: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    textField: {
    //   marginLeft: theme.spacing(1),
    //   marginRight: theme.spacing(1),
    //   width: 200,
    },
    styleTd:{
        verticalAlign:"inherit"
    }
  }));
const FirstStage=forwardRef((props,ref)=> {
    const [selectedDate, setSelectedDate] = useState(new Date('2014-08-18T21:11:54'));
    const [documentState, setDocumentState] = useState("")
    const [selectState, setSelectState] = useState("")

    const classes = useStyles();

    const handleDateChange = (date) => {
      setSelectedDate(date);
    };
    useImperativeHandle(ref,()=>({
        getAlert(){
            console.log(selectState)
        }
    }))
    return (
        <div>
           <table
           style={{borderSpacing:"10px 10px",borderCollapse:"separate",verticalAlign:"inherit",display:"inline-block"}}
           >
               <tbody>
                   <tr>
                       <td className={classes.styleTd}>
                           <p> document text</p>
                       </td>
                       <td className={classes.styleTd}>
                           <TextField value={documentState} onChange={(e)=>setDocumentState(e.target.value)} fullWidthid="filled-basic" label="Filled" variant="filled" />
                       </td>

                   </tr>
                   <tr>
                       <td className={classes.styleTd}>
                           Select
                       </td>
                       <td className={classes.styleTd}> 
                           <Select
                           fullWidthid="filled-basic" label="Filled" variant="filled"
                           fullWidth
                           value={selectState}
                           onChange={(e)=>setSelectState(e.target.value)}>
                                <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
                           </Select>
                       </td>

                   </tr>
                   <tr>
                       <td className={classes.styleTd}>
                           date
                       </td>
                       <td className={classes.styleTd}>
                       <TextField
                       fullWidth
                       fullWidthid="filled-basic" label="Filled" variant="filled"

        id="date"
        label="Birthday"
        type="date"
        defaultValue="2017-05-24"
        className={classes.textField}
        InputLabelProps={{
          shrink: true,
        }}/>

                       </td>
                   </tr>
               </tbody>
           </table>
        </div>
    )
})


export default FirstStage
